class Solution {
public:
    int minNumberOperations(vector<int>& target) {
        int res = 0;
        vector<int> increase;
        target.push_back(0);
        for (auto x: target){
            if (!increase.empty() && increase.back() > x)
                res += increase.back() - x;
            while (!increase.empty() && increase.back() >= x)
                increase.pop_back();
            increase.push_back(x);
        }
        return res;
    }
};

```

